       , oakley15
       , oakley16
       , oakley17
       , oakley18
         -- * DH Types
       , DHOakley1(..)
       , DHOakley14(..)
       ) where

import Foreign.Storable
import Raaz.Number

-- | Group used in DH computation
data Group w = Group { prime     :: w -- ^ Large safe prime: p
                     , generator :: w -- ^ Generator: g
                     , order     :: w -- ^ Prime order: q
                     } deriving Show

